import React, {useState, useEffect} from 'react'
// import {Link, Route, Routes } from 'react-router-dom';
import AddTopicComponent from '../components/AddTopicComponent';
import Genre from '../components/Genre';
import Structure from '../components/Structure';
import Display from '../components/Display';
import { GPT3 } from '../components/GPT3';
import { Button } from 'react-bootstrap';


export default function Home() {
  const [topicList, setTopicList] = useState([]);
  const [items, setItems] = useState([]);
  const [generatedText, setGeneratedText] = useState('');


  async function fetchData() {
    const text = await GPT3(`Generate me song lyrics with these topics ${topicList}, and this structure ${items}`);
    setGeneratedText(text);
  }

  useEffect(() => {
    fetchData();
  });








  return (
      <div className="Home">
        <Genre />
        <Structure items = {items} setItems = {setItems} />
        <AddTopicComponent topicList = {topicList} setTopicList = {setTopicList} />
        {/* <Display displayText = {generatedText} /> */}
        <Button className='generate' onClick={fetchData}>Generate Text</Button>
        <p>{generatedText}</p>
      </div>
  )
}
