import {React, useState} from 'react'

function Genre() {
      // The initial state for the dropdown is closed
  const [isOpen, setIsOpen] = useState(false);

  // This function opens the dropdown menu
  function openDropdown() {
    setIsOpen(true);
  }

  // This function closes the dropdown menu
  function closeDropdown() {
    setIsOpen(false);
  }

  return (
    <div onMouseEnter={openDropdown} onMouseLeave={closeDropdown}>
      <button>Menu</button>
      {isOpen && (
        <ul>
          <li>Option 1</li>
          <li>Option 2</li>
          <li>Option 3</li>
        </ul>
      )}
    </div>
  );
}

export default Genre
