import React from 'react';
import { Button } from 'react-bootstrap';

const GreenButton = () => {
  const styles = {
    width: '100px',
    margin: '0 auto',
    display: 'block'
  };

  const handleClick = () => {
    // Get the current body element and update its background color
    const body = document.querySelector('body');
    body.style.backgroundColor = 'green';
  }

  return (
    <Button variant="primary" style={styles} onClick={handleClick}>
      Turn green
    </Button>
  );
}

export default GreenButton;
