import { Router, Link } from 'react-router-dom'

export default function LoginComponent() {

  const [username, setUsername] = useState('');

  return (
    <div className="LoginComponent">
        <input type="text" className='LoginComponent--name' placeholder='Topic' onChange={setUsername()} /> 
        <Link to ='/home'><button className='LoginComponent--button'>Login</button></Link>
    </div>
  )
}
