import {React, useState} from 'react'
import { Router, Link } from 'react-router-dom'

export default function AddTopicComponent() {

  const [inputValue, setInputValue] = useState('');
  const [list, setList] = useState([]);


  const handleClick = () => {
    // Add the inputValue to the list
    setList([...list, inputValue]);

    // Clear the input box
    setInputValue('');
  }

  // const [username, setUsername] = useState('');

  return (
    <div className="AddTopicComponent">
        <input type="text" className='AddTopicComponent--name' placeholder='Topic'value={inputValue} onChange={e => setInputValue(e.target.value)}  />
        <button className='AddTopicComponent--button' onClick={handleClick}>Add</button>
        <button></button>



    </div>
  )
}
