import Dropdown from 'react-bootstrap/Dropdown';
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';  



function Genre() {
  const [numButtons, setNumButtons] = useState(0);

  const handleClick = () => {
    // Increment the number of buttons by 1
    setNumButtons(numButtons + 1);
  }

  return (

    <div>
      <button onClick={handleClick}>Add button</button>
      {/* Render the number of buttons specified by the state */}
      {[...Array(numButtons)].map((_, i) => (
        <button key={i}>Button {i + 1}</button>
      ))}
    </div>
  );
}

export default Genre;
